
public class Control3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int score = 60;
		int score1 = 70;
		int score2 = 80;
		int score3 = 50;
		int score4 = 30;
		int a = score+score1+score2+score3+score4;
		float b = a/5.0f;
		
		System.out.println("나잘난의 성적 총합계는 "+ a + "점입니다." + "평균 성적은 "+b+"점입니다.");

		
		
		int x = 2;
		if (x%2 == 0)
		System.out.println("짝수입니다.");
		
		else
		System.out.println("홀수입니다.");
		
	}

}
